import { Component, OnInit } from '@angular/core';
import {Menu} from './menu';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.scss']
})
export class MenuComponent implements OnInit {

  public menuProperties: Array<Menu> = [
    {
    id: '1',
    titre: 'Tableau de bord',
    icon: '',
    url: '',
    sousMenu: [
      {
        id: '11',
        titre: 'Vue d\'ensemble',
        icon: '',
        url: ''
      },
      {
        id: '12',
        titre: 'Statistiques',
        icon: '',
        url: ''
      }
    ]
  },
    {
      id: '2',
      titre: 'Articles',
      icon: '',
      url: '',
      sousMenu: [
        {
          id: '21',
          titre: 'Articles',
          icon: '',
          url: ''
        },
        {
          id: '22',
          titre: 'Mouvements du stock',
          icon: '',
          url: ''
        }
      ]
    },
    {
      id: '3',
      titre: 'Clients',
      icon: '',
      url: '',
      sousMenu: [
        {
          id: '31',
          titre: 'Clients',
          icon: '',
          url: ''
        },
        {
          id: '32',
          titre: 'Commandes clients',
          icon: '',
          url: ''
        }
      ]
    },
    {
      id: '4',
      titre: 'Fournisseurs',
      icon: '',
      url: '',
      sousMenu: [
        {
          id: '41',
          titre: 'Fournisseurs',
          icon: '',
          url: ''
        },
        {
          id: '42',
          titre: 'Commandes fournisseurs',
          icon: '',
          url: ''
        }
      ]
    },
    {
      id: '5',
      titre: 'Parametrages',
      icon: '',
      url: '',
      sousMenu: [
        {
          id: '51',
          titre: 'Categories',
          icon: '',
          url: ''
        },
        {
          id: '52',
          titre: 'Utilisateurs',
          icon: '',
          url: ''
        }
      ]
    }
  ];

  ngOnInit(): void {
  }

}
<div class="accordion" id="accordionExample">
    <!-- Boucle pour chaque élément de menu -->
    <div *ngFor="let menu of menuProperties" class="accordion-item">

        <!-- Bouton d'accordéon pour le menu actuel -->
        <h2 class="accordion-header">
            <button class="accordion-button collapsed" 
                    type="button" 
                    data-bs-toggle="collapse" 
                    [attr.data-bs-target]="'#collapse' + menu.id" 
                    aria-expanded="false" 
                    [attr.aria-controls]="'collapse' + menu.id">
                {{menu.titre}}
            </button>
        </h2>

        <!-- Contenu du sous-menu pour le menu actuel -->
        <div [id]="'collapse' + menu.id" 
             class="accordion-collapse collapse" 
             data-bs-parent="#accordionExample">
            <!-- Boucle pour chaque élément de sous-menu -->
            <div *ngFor="let sousMenu of menu.sousMenu">
                {{sousMenu.titre}}
            </div>
        </div>

    </div>
</div>
